import React, {useContext} from "react"
import {Context} from "../Context"
import filmIcon from "../images/film-icon.png"



export default function Results() {
    const {searchSubmit, renderSearchResults, movieData} = useContext(Context)

    function toggleWatchIcon(id) {
        const alreadyInWatchlist = watchlist.some(film => film.id === id)
        if (alreadyInWatchlist) {
            return <button onClick={() => removeFromWatchlist(film.id)}><img src={greenCheck} alt="" />Remove From Watchlist</button>
        } else {
            return <button onClick={() => addToWatchlist(film)}><img src={addIcon} alt=""/>Add To Watchlist</button>
        }
    }

    if (searchSubmit === true) {
        return (
            <>
                {renderSearchResults(movieData)}
            </>
        )
    } else {
        return (
            <>
                <div className="bottom-container">
                    <img src={filmIcon} className="empty-search" alt="" />
                    <p className="subtext empty-search">Start exploring</p>
                </div>
            </>
        )
    }

}
